mapper.update:
update member
set email=#{email}, age=#{age}, gender=#{gender}, phone=#{phone}, address=#{address}, modify_date=sysdate
where user_id=#{userId}

mapper.delete:
update member
set status='N'
where user_id=#{userId}

mapper.update:
UPDATE MEMBER SET
USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}
AND STATUS='Y'

mapper.select:
select count(*)
from member
where user_id=#{userId}
